import { TabContent, TabPane, Nav, NavItem, NavLink, CardBody, Card, Button, CardTitle, CardText, Row, Col } from 'reactstrap';
import {fetchAPI} from '../lib/utility';
import React, { useState } from 'react';
import classnames from 'classnames';

export default ({allResourceData}) => {  
  const articles = <Row>
      {
        allResourceData.map((res)=>{
          return(
            <Col xs="6" sm="4" key={res._id}>
              <Card style={{ margin: "0 0.5rem 20px 0.5rem" }}>
                <CardBody>
                  <CardTitle>
                    {res.topic}<br/>
                    <hr className="my-2" />
                    <div className='published'>{(res.published_at).toDateString()}</div>
                    <div className='author'>{res.author}</div>
                  </CardTitle>
                  <CardText className='content'>{res.content}</CardText>
                </CardBody>
                <div className="card-footer">
                  <button type="button" class="btn btn-info btn-lg" data-toggle="modal" data-target="#myModal">Open Modal</button>
                  <div class="modal fade" id="myModal" role="dialog">
                    <div class="modal-dialog">
                      <div class="modal-content">
                        <div class="modal-header">
                          <button type="button" class="close" data-dismiss="modal">&times;</button>
                          <h4 class="modal-title">{res.topic}</h4>
                        </div>
                        <div class="modal-body">
                          <p>{res.content}</p>
                        </div>
                        <div class="modal-footer">
                          <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>
                        </div>
                      </div>

                    </div>
                  </div>
                </div>
              </Card>
            </Col>
          );
      })
      }
    <style jsx global>
      {`
        a {
          color: white;
        }
        a:link {
          text-decoration: none;
          color: white;
        }
        a:hover {
          color: white;
        }
        .content{
          overflow:hidden;
          white-space: nowrap; 
          overflow: hidden;
          text-overflow: ellipsis; 
          border: 1px solid #000000;
        }
        .published{
          float:right
        }
        .author{
          float:left
        }
      `}
    </style>    
  </Row>
  const [activeTab, setActiveTab] = useState('1');

    const toggle = tab => {
      if(activeTab !== tab) setActiveTab(tab);
    }

    return (
      <div>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '1' })}
              onClick={() => { toggle('1'); }}
            >
              Articles
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '2' })}
              onClick={() => { toggle('2'); }}
            >
              Videos
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: activeTab === '3' })}
              onClick={() => { toggle('3'); }}
            >
              Tutorials
            </NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={activeTab}>
          <TabPane tabId="1">
            {articles}
          </TabPane>
          <TabPane tabId="2">

          </TabPane>
          <TabPane tabId="3">
            <Row>
              <Col sm="12">
                <h4>Tab 3 Contents</h4>
              </Col>
            </Row>
          </TabPane>
        </TabContent>
      </div>
    );
};


export async function getStaticProps() {
    const allResourceData = await fetchAPI('/tutorials');
    return {
      props: {
        allResourceData
      }
    }
  }
