import React, { useContext } from "react";
import Head from "next/head";
import Link from "next/link";
import { Container, Nav, NavItem } from "reactstrap";
import { logout } from "../lib/auth";
import AppContext from "../context/AppContext";

const Layout = (props) => {
  const title = "Quantumniac's";
  const { user, setUser } = useContext(AppContext);

  function myFunction() {
    var x = document.getElementById("myTopnav");
    if (x.className === "topnav") {
      x.className += " responsive";
    } else {
      x.className = "topnav";
    }
  }


  return (
    <div>
      <Head>
        <title>{title}</title>  
        <meta charSet="utf-8" />
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
          integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
          crossOrigin="anonymous"
        />

        <script src="https://js.stripe.com/v3" />
      </Head>
      <header>
        <style jsx>
          {`

          `}
        </style>
        <nav class="navbar navbar-expand-lg navbar-light bg-light">
          <a class="navbar-brand" href="#">Quantumniac's</a>
          <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>

          <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav mr-auto">
              <li class="nav-item active">
                <Link>
                  <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
                </Link>
              </li>
              <li class="nav-item">
                <Link href="/events"><a class="nav-link" href="#">Events</a></Link>
              </li>
              <li class="nav-item">
                <Link href="/resources"><a class="nav-link" href="#">Resources</a></Link>
              </li>
              <li class="nav-item">
                <Link href="/chats"><a class="nav-link" href="#">Notices</a></Link>
              </li>
              <li class="nav-item">
                <Link href="/contact"><a class="nav-link" href="#">Contact</a></Link>
              </li>
              <li class="nav-item">
                <Link href="/about"><a class="nav-link" href="#">About</a></Link>
              </li>

              <li class="nav-item">
                  {user ? (
                      <h5>{user.username}</h5>
                    ) : (
                      <Link href="/register">
                        <a className="nav-link"> Sign up</a>
                      </Link>
                    )}
              </li>
              <li class="nav-item">
              {user ? (
                <Link href="/">
                  <a
                    className="nav-link"
                    onClick={() => {
                      logout();
                      setUser(null);
                    }}
                  >
                    Logout
                  </a>
                </Link>
                  ) : (
                    <Link href="/login">
                      <a className="nav-link">Sign in</a>
                    </Link>
              )}
              </li>
            </ul>

          </div>
        </nav>


      </header>
      <Container>{props.children}</Container>
    </div>
  );
};

export default Layout ;
