const nodemailer = require('nodemailer');
const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
export async function fetchAPI(id) {
    try {
        const response = await fetch('https://community-app-strapi-backend.herokuapp.com'+id, {
            method: 'GET',
            credentials: 'same-origin'
        });
        const exam = await response.json();
        return exam;
    } catch (error) {
        console.error(error);
    }
}
export  function sendMail(senderAddress, Message){
    var transport = nodemailer.createTransport({
        host: 'smtp.mailtrap.io',
        port: 2525,
        auth: {
           user: '2bd4810e6cb2d2',
           pass: '42a18b35022fd6'
        }
    });
    const message = {
        from: senderAddress, // Sender address
        to: 'community-manager@gmail.com',         // List of recipients
        subject: 'Query from Community Webpage', // Subject line
        text: Message // Plain text body
    };
    transport.sendMail(message, function(err, info) {
        if (err) {
          alert(err);
        } else {
          console.log(info);
        }
    });
}
